package roadshop.web.board.vo;

/*이 부분을 오늘 수정해야 함!!*/

import java.sql.Timestamp;

public class BoardInfo {
	private int b_num;
	private String b_writer;
	private String b_subject;
	private String b_content;
	private String b_image;
	private Timestamp b_reg_date;
	private int readcount;
	private int ref;
	private int re_step;
	private int re_level;
	private String i_id;
	private String m_id;
	
	public BoardInfo() {
		// TODO Auto-generated constructor stub
	}
	
	public BoardInfo(int b_num, String b_writer, String b_subject,
			String b_content, String b_image, Timestamp b_reg_date,
			int readcount, int ref, int re_step, int re_level, String i_id,
			String m_id) {
		super();
		this.b_num = b_num;
		this.b_writer = b_writer;
		this.b_subject = b_subject;
		this.b_content = b_content;
		this.b_image = b_image;
		this.b_reg_date = b_reg_date;
		this.readcount = readcount;
		this.ref = ref;
		this.re_step = re_step;
		this.re_level = re_level;
		this.i_id = i_id;
		this.m_id = m_id;
	}

	public BoardInfo(int b_num, String b_writer, String b_subject,
			String b_content, String b_image, int readcount, 
			int ref, int re_step, int re_level, String i_id,
			String m_id) {
		super();
		this.b_num = b_num;
		this.b_writer = b_writer;
		this.b_subject = b_subject;
		this.b_content = b_content;
		this.b_image = b_image;
		this.readcount = readcount;
		this.ref = ref;
		this.re_step = re_step;
		this.re_level = re_level;
		this.i_id = i_id;
		this.m_id = m_id;
	}
	
	public String getB_image() {
		return b_image;
	}
	public void setB_image(String b_image) {
		this.b_image = b_image;
	}
	public String getI_id() {
		return i_id;
	}
	public void setI_id(String i_id) {
		this.i_id = i_id;
	}
	public String getM_id() {
		return m_id;
	}
	public void setM_id(String m_id) {
		this.m_id = m_id;
	}
	public int getB_num() {
		return b_num;
	}
	public void setB_num(int b_num) {
		this.b_num = b_num;
	}
	public String getB_writer() {
		return b_writer;
	}
	public void setB_writer(String b_writer) {
		this.b_writer = b_writer;
	}
	public String getB_subject() {
		return b_subject;
	}
	public void setB_subject(String b_subject) {
		this.b_subject = b_subject;
	}
	public String getB_content() {
		return b_content;
	}
	public void setB_content(String b_content) {
		this.b_content = b_content;
	}
	public Timestamp getB_reg_date() {
		return b_reg_date;
	}
	public void setB_reg_date(Timestamp b_reg_date) {
		this.b_reg_date = b_reg_date;
	}
	public int getReadcount() {
		return readcount;
	}
	public void setReadcount(int readcount) {
		this.readcount = readcount;
	}
	public int getRef() {
		return ref;
	}
	public void setRef(int ref) {
		this.ref = ref;
	}
	public int getRe_step() {
		return re_step;
	}
	public void setRe_step(int re_step) {
		this.re_step = re_step;
	}
	public int getRe_level() {
		return re_level;
	}
	public void setRe_level(int re_level) {
		this.re_level = re_level;
	}	
}
